import React, { useEffect, useRef } from "react";
import { Animated, TouchableOpacity, View, Text } from "react-native";
import FontAwesome from "@expo/vector-icons/FontAwesome";
import { Tabs } from "expo-router";
import { Header } from "../../src/components";
import IconSvg from "../../src/components/IconSvg";
import { windowWidth } from "../../src/constants/ScreenWidth";
import { useSelector } from "react-redux";
import {
  selectActualLanguage,
  selectWordsLang,
} from "../../src/store/selectors";

function MyTabBar({ state, descriptors, navigation }) {
  const translateX = useRef(new Animated.Value(0)).current;

  useEffect(() => {
    Animated.spring(translateX, {
      toValue: (state.index * windowWidth) / 5, // Assuming each tab has equal width of 100
      useNativeDriver: true,
    }).start();
  }, [state.index]);

  return (
    <View
      style={{
        flexDirection: "row",
        width: "100%",
        backgroundColor: "#F1F1F1",
        paddingVertical: 10,
        position: "relative",
      }}
    >
      <View
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: windowWidth, // Assuming each tab has equal width of 100
          height: 4,
          backgroundColor: "#ECF0FE",
        }}
      />
      <Animated.View
        style={{
          position: "absolute",
          top: 0,
          left: 0,
          width: windowWidth / 5, // Assuming each tab has equal width of 100
          height: 4,
          backgroundColor: "#35498e",
          transform: [{ translateX }],
        }}
      />

      {state.routes.map((route, index) => {
        const { options } = descriptors[route.key];
        const isFocused = state.index === index;
        const {
          tabBarIcon,
          tabBarActiveTintColor,
          tabBarInactiveTintColor,
          title,
        } = options || {};

        const onPress = () => {
          const event = navigation.emit({
            type: "tabPress",
            target: route.key,
            canPreventDefault: true,
          });

          if (!isFocused && !event.defaultPrevented) {
            navigation.navigate({ name: route.name, merge: true });
          }
        };

        const onLongPress = () => {
          navigation.emit({
            type: "tabLongPress",
            target: route.key,
          });
        };

        if (options.title) {
          return (
            <TouchableOpacity
              key={index}
              accessibilityRole="button"
              accessibilityState={isFocused ? { selected: true } : {}}
              accessibilityLabel={options.tabBarAccessibilityLabel}
              testID={options.tabBarTestID}
              onPress={onPress}
              onLongPress={onLongPress}
              style={{
                flex: 1,
                alignItems: "center",
                justifyContent: "center",
              }}
            >
              <View
                style={{
                  flexDirection: "column",
                  alignItems: "center",
                  gap: 5,
                }}
              >
                {tabBarIcon && (
                  <IconSvg width={36} height={36} icon={tabBarIcon} />
                )}
                <Text
                  style={{
                    fontFamily: "MuseoSans_500",
                    fontSize: 12,
                    color: isFocused
                      ? tabBarActiveTintColor
                      : tabBarInactiveTintColor,
                  }}
                >
                  {title || route.name}
                </Text>
              </View>
            </TouchableOpacity>
          );
        }
      })}
    </View>
  );
}

