    Promise.all([
      fetchBogotaDrplV2("/eventos-destacados-app", actualLanguage),
      fetchBogotaDrplV2("/tax/categorias_atractivos_2024/all", actualLanguage),
      fetchBogotaDrplV2("/rt/all", actualLanguage),
      fetchBogotaDrplV2("/appinfo", actualLanguage),
      fetchBogotaDrplV2("/blog/all/all", actualLanguage),
    ])
      .then(([eventsData, categories, rutas, infoGnrl, blog]) => {
        setBlog(blog);
        setLinkVideo(infoGnrl[0].field_link_video_home);

        setBogNatural(
          categories.map((cat) => ({
            name: cat.name,
            tid: cat.tid,
            field_cover_image: cat.field_banner_prod,
            field_categor: cat.field_categor,
          }))
        );
        setRutas(rutas);

        function setMidnight(dateString) {
          const date = new Date(dateString);
          date.setHours(0, 0, 0, 0);
          return date;
        }

        function compararFechas(a, b) {
          // Si el evento no tiene fecha de finalización, usar la fecha de inicio
          const endDateA = a.field_end_date
            ? a.field_end_date.length === 10
              ? setMidnight(a.field_end_date)
              : new Date(a.field_end_date)
            : setMidnight(a.field_date);
          const endDateB = b.field_end_date
            ? b.field_end_date.length === 10
              ? setMidnight(b.field_end_date)
              : new Date(b.field_end_date)
            : setMidnight(b.field_date);

          return endDateA - endDateB;
        }
        // Ordenar el arreglo por fecha de finalización
        eventsData.sort(compararFechas);
        setEventsProx(eventsData);
        // Marcar que las consultas han finalizado
        setQueriesCompleted(true);
      })
      .catch((error) => console.error(error));
