    const parseMarkdown = (text) => {
      // Verificar si el input no es texto o est√° vac√≠o
      if (typeof text !== "string" || text.trim() === "") {
        return null;
      }

      // Encabezados (## Header)
      text = text.replace(/## (.+)/g, "## $1"); // Indica que es un encabezado de nivel 2
      text = text.replace(/# (.+)/g, "# $1"); // Indica que es un encabezado de nivel 1

      // Reemplazar saltos de l√≠nea con nuevas l√≠neas
      text = text.replace(/\n/g, "\n");

      // Eliminar enlaces ([text](url))
      text = text.replace(/\[.+?\]\(https?:\/\/[^\s]+\)/g, "");

      // Dividir en partes por negritas e it√°licas
      const parts = text.split(/(\*\*.+?\*\*|\*.+?\*)/g);

      return (
        <Text>
          {parts.map((part, index) => {
            // Negritas
            if (part.match(/\*\*(.+?)\*\*/)) {
              return (
                <Text key={index} style={{ fontWeight: "bold" }}>
                  {part.replace(/\*\*/g, "")}
                </Text>
              );
            }

            // It√°licas
            if (part.match(/\*(.+?)\*/)) {
              return (
                <Text key={index} style={{ fontStyle: "italic" }}>
                  {part.replace(/\*/g, "")}
                </Text>
              );
            }

            // Texto normal
            return <Text key={index}>{part}</Text>;
          })}
        </Text>
      );
    };

